package net.civeira.phylax.features.access.tenantconfig.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class ForceMfaVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property force mfa of tenant config ")
  void test_force_mfa_v_o_builder() {
    Assertions.assertThrows(ConstraintException.class, () -> ForceMfaVO.tryFrom(null));
    Assertions.assertEquals(true, ForceMfaVO.from(true).isForceMfa());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> ForceMfaVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + true + "]", ForceMfaVO.from(true).toString());
  }
}
