package net.civeira.phylax.features.access.tenantconfig.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class RecoverPassEmailVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property recover pass email of tenant config ")
  void test_recover_pass_email_v_o_builder() {
    Assertions.assertFalse(RecoverPassEmailVO.nullValue().getRecoverPassEmail().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(RecoverPassEmailVO.tryFrom(null).getRecoverPassEmail().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals("one",
        RecoverPassEmailVO.from("one").getRecoverPassEmail().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> RecoverPassEmailVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", RecoverPassEmailVO.from("one").toString());
  }
}
