package net.civeira.phylax.features.access.tenantconfig.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class RegisterdEmailVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property registerd email of tenant config ")
  void test_registerd_email_v_o_builder() {
    Assertions.assertFalse(RegisterdEmailVO.nullValue().getRegisterdEmail().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(RegisterdEmailVO.tryFrom(null).getRegisterdEmail().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals("one", RegisterdEmailVO.from("one").getRegisterdEmail().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> RegisterdEmailVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", RegisterdEmailVO.from("one").toString());
  }
}
