package net.civeira.phylax.features.access.tenantconfig.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class WellcomeEmailVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property wellcome email of tenant config ")
  void test_wellcome_email_v_o_builder() {
    Assertions.assertFalse(WellcomeEmailVO.nullValue().getWellcomeEmail().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(WellcomeEmailVO.tryFrom(null).getWellcomeEmail().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals("one", WellcomeEmailVO.from("one").getWellcomeEmail().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> WellcomeEmailVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", WellcomeEmailVO.from("one").toString());
  }
}
