package net.civeira.phylax.features.access.tenantconfig.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class InnerLabelVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property inner label of tenant config ")
  void test_inner_label_v_o_builder() {
    Assertions.assertFalse(InnerLabelVO.nullValue().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(InnerLabelVO.tryFrom(null).getValue().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals("one", InnerLabelVO.from("one").getValue().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> InnerLabelVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", InnerLabelVO.from("one").toString());
  }
}
