package net.civeira.phylax.features.access.tenantloginprovider.domain.gateway;

import java.util.List;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

class TenantLoginProviderCursorUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test a entity reference contruction")
  void test_tenant_login_provider_cursor_builder() {
    TenantLoginProviderCursor cursor = TenantLoginProviderCursor.builder().build();
    Assertions.assertFalse(cursor.getLimit().isPresent(),
        "Limit must be empty if cursor is build without it");
    Assertions.assertFalse(cursor.getSinceUid().isPresent(),
        "Since must be empty if cursor is build without it");
    cursor.setLimit(100);
    Assertions.assertEquals(100, cursor.getLimit().get(), "Limit must be the same as the assigned");
    cursor.setSinceUid("one");
    Assertions.assertEquals("one", cursor.getSinceUid().get(),
        "Since Uid must be the same as the assigned");
    Assertions.assertTrue(cursor.getOrder().isEmpty(), "The order must be empty");
    cursor.setOrder(List.of(TenantLoginProviderOrder.NAME_ASC, TenantLoginProviderOrder.NAME_DESC));
    Assertions.assertEquals(
        List.of(TenantLoginProviderOrder.NAME_ASC, TenantLoginProviderOrder.NAME_DESC),
        cursor.getOrder(), "The order must be the same that was assinged");
    Assertions.assertTrue(cursor.getSinceName().isEmpty(),
        "The value since for Name must be empty");
    cursor.setSinceName("one");
    Assertions.assertEquals("one", cursor.getSinceName().get(),
        "The value since for Name must be the same that was assinged");
  }
}
