package net.civeira.phylax.features.access.tenantloginprovider.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class MetadataVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property metadata of tenant login provider ")
  void test_metadata_v_o_builder() {
    Assertions.assertFalse(MetadataVO.nullValue().getMetadata().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(MetadataVO.tryFrom(null).getMetadata().isPresent(),
        "With a null value, no present result");
    Assertions.assertThrows(ConstraintException.class,
        () -> MetadataVO.tryFrom(Integer.valueOf(1)));
    Assertions.assertEquals("file_1", MetadataVO.from("file_1").getMetadata().orElse(null));
    Assertions.assertEquals("[" + "file_1" + "]", MetadataVO.fromTemporal("file_1").toString());
    Assertions.assertFalse(MetadataVO.from("file_1").isTemporal());
    Assertions.assertTrue(MetadataVO.fromTemporal("file_1").isTemporal());
    Assertions.assertEquals("[" + "file_1" + "]", MetadataVO.from("file_1").toString());
    Assertions.assertFalse(MetadataVO.tryFrom("1").isTemporal());
    Assertions.assertTrue(MetadataVO.tryFrom("temp://1").isTemporal());
  }
}
