package net.civeira.phylax.features.access.tenanttermsofuse.domain;

import java.time.LocalDateTime;
import java.time.ZoneId;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.features.access.tenant.domain.TenantReference;

class TenantTermsOfUseChangeSetUnitTest {

  /**
   * @autogenerated CachedGenerator
   */
  @Test
  @DisplayName("Test a entity reference contruction")
  void test_tenant_terms_of_use_change_set_builder() {
    TenantTermsOfUseChangeSet one =
        new TenantTermsOfUseChangeSet().uid("one").tenant(TenantReference.of("one")).text("one")
            .enabled(true).attached("file_1").activationDate(LocalDateTime.of(1980, 8, 20, 0, 0)
                .atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime())
            .version(1).newUid();
    one.uid("two");
    Assertions.assertEquals("two", one.getUid().get(), "Value must equals to the assigned value");
    one.unset("uid");
    Assertions.assertFalse(one.getUid().isPresent(), "After unset, the valeue must be dissapear");
    Assertions.assertEquals(TenantReference.of("one"), one.getTenant().get(),
        "Value must equals to the intial value");
    one.tenant(TenantReference.of("two"));
    Assertions.assertEquals(TenantReference.of("two"), one.getTenant().get(),
        "Value must equals to the assigned value");
    one.unset("tenant");
    Assertions.assertFalse(one.getTenant().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals("one", one.getText().get(), "Value must equals to the intial value");
    one.text("two");
    Assertions.assertEquals("two", one.getText().get(), "Value must equals to the assigned value");
    one.unset("text");
    Assertions.assertFalse(one.getText().isPresent(), "After unset, the valeue must be dissapear");
    Assertions.assertEquals(true, one.getEnabled().get(), "Value must equals to the intial value");
    one.enabled(false);
    Assertions.assertEquals(false, one.getEnabled().get(),
        "Value must equals to the assigned value");
    one.unset("enabled");
    Assertions.assertFalse(one.getEnabled().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals("file_1", one.getAttached().get(),
        "Value must equals to the intial value");
    one.attached("file_2");
    Assertions.assertEquals("file_2", one.getAttached().get(),
        "Value must equals to the assigned value");
    one.unset("attached");
    Assertions.assertFalse(one.getAttached().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals(
        LocalDateTime.of(1980, 8, 20, 0, 0).atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime(),
        one.getActivationDate().get(), "Value must equals to the intial value");
    one.activationDate(
        LocalDateTime.of(1981, 9, 6, 0, 0).atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime());
    Assertions.assertEquals(
        LocalDateTime.of(1981, 9, 6, 0, 0).atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime(),
        one.getActivationDate().get(), "Value must equals to the assigned value");
    one.unset("activationDate");
    Assertions.assertFalse(one.getActivationDate().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals(0, one.getVersion().get(), "Value must equals to the intial value");
    one.version(2);
    Assertions.assertEquals(2, one.getVersion().get(), "Value must equals to the assigned value");
    one.unset("version");
    Assertions.assertFalse(one.getVersion().isPresent(),
        "After unset, the valeue must be dissapear");
  }
}
