package net.civeira.phylax.features.access.tenanttermsofuse.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class AttachedVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property attached of tenant terms of use ")
  void test_attached_v_o_builder() {
    Assertions.assertFalse(AttachedVO.nullValue().getAttached().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(AttachedVO.tryFrom(null).getAttached().isPresent(),
        "With a null value, no present result");
    Assertions.assertThrows(ConstraintException.class,
        () -> AttachedVO.tryFrom(Integer.valueOf(1)));
    Assertions.assertEquals("file_1", AttachedVO.from("file_1").getAttached().orElse(null));
    Assertions.assertEquals("[" + "file_1" + "]", AttachedVO.fromTemporal("file_1").toString());
    Assertions.assertFalse(AttachedVO.from("file_1").isTemporal());
    Assertions.assertTrue(AttachedVO.fromTemporal("file_1").isTemporal());
    Assertions.assertEquals("[" + "file_1" + "]", AttachedVO.from("file_1").toString());
    Assertions.assertFalse(AttachedVO.tryFrom("1").isTemporal());
    Assertions.assertTrue(AttachedVO.tryFrom("temp://1").isTemporal());
  }
}
