package net.civeira.phylax.features.access.tenanttermsofuse.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class TextVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property text of tenant terms of use ")
  void test_text_v_o_builder() {
    Assertions.assertThrows(ConstraintException.class, () -> TextVO.tryFrom(null));
    Assertions.assertEquals("one", TextVO.from("one").getText());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> TextVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", TextVO.from("one").toString());
  }
}
