package net.civeira.phylax.features.access.trustedclient.domain.valueobject;

import java.util.List;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;
import net.civeira.phylax.features.access.trustedclient.domain.AllowedRedirects;

class AllowedRedirectsVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property allowed redirects of trusted client ")
  void test_allowed_redirects_v_o_builder() {
    Assertions.assertTrue(AllowedRedirectsVO.tryFrom(null).getAllowedRedirects().isEmpty());
    List<AllowedRedirects> list = List.of();
    Assertions.assertEquals(list, AllowedRedirectsVO.from(list).getAllowedRedirects());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> AllowedRedirectsVO.tryFrom(falingInstance));
    Assertions.assertEquals(
        "[" + List.of(AllowedRedirects.builder().uid("one").url("one").version(1).build()) + "]",
        AllowedRedirectsVO
            .from(List.of(AllowedRedirects.builder().uid("one").url("one").version(1).build()))
            .toString());
  }
}
