package net.civeira.phylax.features.access.trustedclient.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class PublicAllowVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property public allow of trusted client ")
  void test_public_allow_v_o_builder() {
    Assertions.assertThrows(ConstraintException.class, () -> PublicAllowVO.tryFrom(null));
    Assertions.assertEquals(true, PublicAllowVO.from(true).getValue());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> PublicAllowVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + true + "]", PublicAllowVO.from(true).toString());
  }
}
