package net.civeira.phylax.features.access.trustedclient.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class UrlVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property url of trusted client allowed redirect ")
  void test_url_v_o_builder() {
    Assertions.assertThrows(ConstraintException.class, () -> UrlVO.tryFrom(null));
    Assertions.assertEquals("one", UrlVO.from("one").getUrl());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> UrlVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", UrlVO.from("one").toString());
  }
}
