package net.civeira.phylax.features.access.trustedclient.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class AllowedRedirectsVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property allowed redirects of trusted client ")
  void test_allowed_redirects_v_o_builder() {
    Assertions.assertFalse(AllowedRedirectsVO.nullValue().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(AllowedRedirectsVO.tryFrom(null).getValue().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals("one", AllowedRedirectsVO.from("one").getValue().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> AllowedRedirectsVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", AllowedRedirectsVO.from("one").toString());
  }
}
