package net.civeira.phylax.features.access.trustedclient.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class CodeVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property code of trusted client ")
  void test_code_v_o_builder() {
    Assertions.assertThrows(ConstraintException.class, () -> CodeVO.tryFrom(null));
    Assertions.assertEquals("one", CodeVO.from("one").getValue());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> CodeVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", CodeVO.from("one").toString());
  }
}
