package net.civeira.phylax.features.access.user.domain;

import java.time.LocalDateTime;
import java.time.ZoneId;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;
import org.mockito.Mockito;

import net.civeira.phylax.common.crypto.AesCipherService;
import net.civeira.phylax.features.access.tenant.domain.TenantReference;

class UserChangeSetUnitTest {

  /**
   * @autogenerated CachedGenerator
   */
  @Test
  @DisplayName("Test a entity reference contruction")
  void test_user_change_set_builder() {
    AesCipherService cypher = Mockito.mock(AesCipherService.class);
    UserChangeSet one = new UserChangeSet().uid("one").tenant(TenantReference.of("one")).name("one")
        .passwordPlain("one").email("one")
        .wellcomeAt(LocalDateTime
            .of(1980, 8, 20, 0, 0).atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime())
        .enabled(true).approve(UserApproveOptions.UNVERIFIED).temporalPassword(true)
        .useSecondFactors(true).secondFactorSeedPlain("one").blockedUntil(LocalDateTime
            .of(1980, 8, 20, 0, 0).atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime())
        .provider("one").version(1).newUid();
    one.uid("two");
    Assertions.assertEquals("two", one.getUid().get(), "Value must equals to the assigned value");
    one.unset("uid");
    Assertions.assertFalse(one.getUid().isPresent(), "After unset, the valeue must be dissapear");
    Assertions.assertEquals(TenantReference.of("one"), one.getTenant().get(),
        "Value must equals to the intial value");
    one.tenant(TenantReference.of("two"));
    Assertions.assertEquals(TenantReference.of("two"), one.getTenant().get(),
        "Value must equals to the assigned value");
    one.unset("tenant");
    Assertions.assertFalse(one.getTenant().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals("one", one.getName().get(), "Value must equals to the intial value");
    one.name("two");
    Assertions.assertEquals("two", one.getName().get(), "Value must equals to the assigned value");
    one.unset("name");
    Assertions.assertFalse(one.getName().isPresent(), "After unset, the valeue must be dissapear");
    Assertions.assertEquals("one", one.getPasswordPlain(cypher).get(),
        "Value must equals to the intial value");
    one.passwordPlain("two");
    Assertions.assertEquals("two", one.getPasswordPlain(cypher).get(),
        "Value must equals to the assigned value");
    one.unset("password");
    Assertions.assertFalse(one.getPasswordPlain(cypher).isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals("one", one.getEmail().get(), "Value must equals to the intial value");
    one.email("two");
    Assertions.assertEquals("two", one.getEmail().get(), "Value must equals to the assigned value");
    one.unset("email");
    Assertions.assertFalse(one.getEmail().isPresent(), "After unset, the valeue must be dissapear");
    Assertions.assertEquals(
        LocalDateTime.of(1980, 8, 20, 0, 0).atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime(),
        one.getWellcomeAt().get(), "Value must equals to the intial value");
    one.wellcomeAt(
        LocalDateTime.of(1981, 9, 6, 0, 0).atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime());
    Assertions.assertEquals(
        LocalDateTime.of(1981, 9, 6, 0, 0).atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime(),
        one.getWellcomeAt().get(), "Value must equals to the assigned value");
    one.unset("wellcomeAt");
    Assertions.assertFalse(one.getWellcomeAt().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals(true, one.getEnabled().get(), "Value must equals to the intial value");
    one.enabled(false);
    Assertions.assertEquals(false, one.getEnabled().get(),
        "Value must equals to the assigned value");
    one.unset("enabled");
    Assertions.assertFalse(one.getEnabled().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals(UserApproveOptions.UNVERIFIED, one.getApprove().get(),
        "Value must equals to the intial value");
    one.approve(UserApproveOptions.PENDING);
    Assertions.assertEquals(UserApproveOptions.PENDING, one.getApprove().get(),
        "Value must equals to the assigned value");
    one.unset("approve");
    Assertions.assertFalse(one.getApprove().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals(true, one.getTemporalPassword().get(),
        "Value must equals to the intial value");
    one.temporalPassword(false);
    Assertions.assertEquals(false, one.getTemporalPassword().get(),
        "Value must equals to the assigned value");
    one.unset("temporalPassword");
    Assertions.assertFalse(one.getTemporalPassword().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals(true, one.getUseSecondFactors().get(),
        "Value must equals to the intial value");
    one.useSecondFactors(false);
    Assertions.assertEquals(false, one.getUseSecondFactors().get(),
        "Value must equals to the assigned value");
    one.unset("useSecondFactors");
    Assertions.assertFalse(one.getUseSecondFactors().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals("one", one.getSecondFactorSeedPlain(cypher).get(),
        "Value must equals to the intial value");
    one.secondFactorSeedPlain("two");
    Assertions.assertEquals("two", one.getSecondFactorSeedPlain(cypher).get(),
        "Value must equals to the assigned value");
    one.unset("secondFactorSeed");
    Assertions.assertFalse(one.getSecondFactorSeedPlain(cypher).isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals(
        LocalDateTime.of(1980, 8, 20, 0, 0).atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime(),
        one.getBlockedUntil().get(), "Value must equals to the intial value");
    one.blockedUntil(
        LocalDateTime.of(1981, 9, 6, 0, 0).atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime());
    Assertions.assertEquals(
        LocalDateTime.of(1981, 9, 6, 0, 0).atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime(),
        one.getBlockedUntil().get(), "Value must equals to the assigned value");
    one.unset("blockedUntil");
    Assertions.assertFalse(one.getBlockedUntil().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals("one", one.getProvider().get(),
        "Value must equals to the intial value");
    one.provider("two");
    Assertions.assertEquals("two", one.getProvider().get(),
        "Value must equals to the assigned value");
    one.unset("provider");
    Assertions.assertFalse(one.getProvider().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals(0, one.getVersion().get(), "Value must equals to the intial value");
    one.version(2);
    Assertions.assertEquals(2, one.getVersion().get(), "Value must equals to the assigned value");
    one.unset("version");
    Assertions.assertFalse(one.getVersion().isPresent(),
        "After unset, the valeue must be dissapear");
  }
}
