package net.civeira.phylax.features.access.user.domain.gateway;

import java.util.List;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.features.access.tenant.domain.TenantReference;

class UserFilterUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test a entity reference contruction")
  void test_user_filter_builder() {
    UserFilter filter = UserFilter.builder().build();
    Assertions.assertFalse(filter.getUid().isPresent(),
        "Uid must be empty if filter is build without it");
    filter.setUid("one");
    Assertions.assertEquals("one", filter.getUid().get(), "Uid must be the same as the assigned");
    Assertions.assertTrue(filter.getUids().isEmpty(),
        "Uid must be empty if filter is build without it");
    filter.setUids(List.of("one"));
    Assertions.assertEquals(List.of("one"), filter.getUids(),
        "Uids must be the same as the assigned");
    Assertions.assertFalse(filter.getSearch().isPresent(),
        "Search must be empty if filter is build without it");
    filter.setSearch("lookup");
    Assertions.assertEquals("lookup", filter.getSearch().get(),
        "Search must be the same as the assigned");
    Assertions.assertFalse(filter.getNameOrEmail().isPresent(),
        "NameOrEmail must be empty if filter is build without it");
    filter.setNameOrEmail("one");
    Assertions.assertEquals("one", filter.getNameOrEmail().get(),
        "NameOrEmail must be the same as the assigned");
    Assertions.assertFalse(filter.getName().isPresent(),
        "Name must be empty if filter is build without it");
    filter.setName("one");
    Assertions.assertEquals("one", filter.getName().get(), "Name must be the same as the assigned");
    Assertions.assertFalse(filter.getTenant().isPresent(),
        "Tenant must be empty if filter is build without it");
    filter.setTenant(TenantReference.of("$ref"));
    Assertions.assertEquals(TenantReference.of("$ref"), filter.getTenant().get(),
        "Tenant must be the same as the assigned");
    Assertions.assertTrue(filter.getTenants().isEmpty(),
        "Tenants must be empty if filter is build without it");
    filter.setTenants(List.of("one"));
    Assertions.assertEquals(List.of("one"), filter.getTenants(),
        "Tenants must be the same as the assigned");
    Assertions.assertFalse(filter.getTenantTenantAccesible().isPresent(),
        "TenantTenantAccesible must be empty if filter is build without it");
    filter.setTenantTenantAccesible("one");
    Assertions.assertEquals("one", filter.getTenantTenantAccesible().get(),
        "TenantTenantAccesible must be the same as the assigned");
  }
}
