package net.civeira.phylax.features.access.user.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;
import net.civeira.phylax.features.access.user.domain.UserApproveOptions;

class ApproveVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property approve of user ")
  void test_approve_v_o_builder() {
    Assertions.assertThrows(ConstraintException.class, () -> ApproveVO.tryFrom("_UNVERIFIED_"));
    Assertions.assertEquals(UserApproveOptions.UNVERIFIED,
        ApproveVO.tryFrom("UNVERIFIED").getApprove().orElse(null));
    Assertions.assertFalse(ApproveVO.nullValue().getApprove().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(ApproveVO.tryFrom(null).getApprove().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals(UserApproveOptions.UNVERIFIED,
        ApproveVO.from(UserApproveOptions.UNVERIFIED).getApprove().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> ApproveVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + UserApproveOptions.UNVERIFIED + "]",
        ApproveVO.from(UserApproveOptions.UNVERIFIED).toString());
  }
}
