package net.civeira.phylax.features.access.user.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class ProviderVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property provider of user ")
  void test_provider_v_o_builder() {
    Assertions.assertFalse(ProviderVO.nullValue().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(ProviderVO.tryFrom(null).getValue().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals("one", ProviderVO.from("one").getValue().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> ProviderVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", ProviderVO.from("one").toString());
  }
}
