package net.civeira.phylax.features.access.user.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class TemporalPasswordVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property temporal password of user ")
  void test_temporal_password_v_o_builder() {
    Assertions.assertFalse(TemporalPasswordVO.nullValue().isTemporalPassword(),
        "A empty vo should have a null value");
    Assertions.assertFalse(TemporalPasswordVO.tryFrom(null).isTemporalPassword(),
        "With a null value, no present result");
    Assertions.assertEquals(true, TemporalPasswordVO.from(true).isTemporalPassword());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> TemporalPasswordVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + true + "]", TemporalPasswordVO.from(true).toString());
  }
}
