package net.civeira.phylax.features.access.user.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class UseSecondFactorsVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property use second factors of user ")
  void test_use_second_factors_v_o_builder() {
    Assertions.assertFalse(UseSecondFactorsVO.nullValue().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(UseSecondFactorsVO.tryFrom(null).getValue().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals(true, UseSecondFactorsVO.from(true).getValue().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> UseSecondFactorsVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + true + "]", UseSecondFactorsVO.from(true).toString());
  }
}
