package net.civeira.phylax.features.access.useracceptedtermnsofuse.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;
import net.civeira.phylax.common.value.validation.ConstraintFailList;
import net.civeira.phylax.features.access.user.domain.UserReference;

class UserVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property user of user accepted termns of use ")
  void test_user_v_o_builder() {
    ConstraintFailList fails = new ConstraintFailList();
    Assertions.assertEquals("10", UserVO.fromReference("10").getReferenceValue(),
        "The builder must store the reference value");
    Assertions.assertEquals("11", UserVO.tryFromReference("11").getReferenceValue(),
        "The builder must store the reference value");
    Assertions.assertEquals("12", UserVO.tryFromReference("12", fails).getReferenceValue(),
        "The builder must store the reference value");
    Assertions.assertTrue(fails.isEmpty());
    Assertions.assertThrows(ConstraintException.class, () -> UserVO.tryFrom(null));
    Assertions.assertEquals(UserReference.of("one"),
        UserVO.from(UserReference.of("one")).getValue());
    Assertions.assertThrows(ConstraintException.class, () -> UserVO.tryFrom(new Object() {}));
    Assertions.assertEquals("[" + UserReference.of("one") + "]",
        UserVO.from(UserReference.of("one")).toString());
  }
}
