package net.civeira.phylax.features.access.useracceptedtermnsofuse.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class VersionVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property version of user accepted termns of use ")
  void test_version_v_o_builder() {
    Assertions.assertFalse(VersionVO.nullValue().getVersion().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(VersionVO.tryFrom(null).getVersion().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals(1, VersionVO.from(1).getVersion().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> VersionVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + 1 + "]", VersionVO.from(1).toString());
  }

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test move to the next value of a version field for user accepted termns of use of user accepted termns of use ")
  void test_version_v_o_next_value() {
    Assertions.assertEquals(1, VersionVO.nullValue().nextVersion());
    Assertions.assertEquals(2, VersionVO.from(1).nextVersion());
  }
}
