package net.civeira.phylax.features.access.useraccesstemporalcode.domain.valueobject;

import java.time.LocalDateTime;
import java.time.ZoneId;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class RecoveryCodeExpirationVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property recovery code expiration of user access temporal code ")
  void test_recovery_code_expiration_v_o_builder() {
    Assertions.assertFalse(
        RecoveryCodeExpirationVO.nullValue().getRecoveryCodeExpiration().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(
        RecoveryCodeExpirationVO.tryFrom(null).getRecoveryCodeExpiration().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals(
        LocalDateTime.of(1980, 8, 20, 0, 0).atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime(),
        RecoveryCodeExpirationVO.from(LocalDateTime.of(1980, 8, 20, 0, 0)
            .atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime()).getRecoveryCodeExpiration()
            .orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> RecoveryCodeExpirationVO.tryFrom(falingInstance));
    Assertions.assertEquals(
        "[" + LocalDateTime.of(1980, 8, 20, 0, 0).atZone(ZoneId.of("Europe/Madrid"))
            .toOffsetDateTime() + "]",
        RecoveryCodeExpirationVO.from(LocalDateTime.of(1980, 8, 20, 0, 0)
            .atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime()).toString());
  }
}
