package net.civeira.phylax.features.access.useraccesstemporalcode.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class RegisterCodeUrlVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property register code url of user access temporal code ")
  void test_register_code_url_v_o_builder() {
    Assertions.assertFalse(RegisterCodeUrlVO.nullValue().getRegisterCodeUrl().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(RegisterCodeUrlVO.tryFrom(null).getRegisterCodeUrl().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals("one", RegisterCodeUrlVO.from("one").getRegisterCodeUrl().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> RegisterCodeUrlVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", RegisterCodeUrlVO.from("one").toString());
  }
}
