package net.civeira.phylax.features.access.useraccesstemporalcode.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class RegisterCodeVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property register code of user access temporal code ")
  void test_register_code_v_o_builder() {
    Assertions.assertFalse(RegisterCodeVO.nullValue().getRegisterCode().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(RegisterCodeVO.tryFrom(null).getRegisterCode().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals("one", RegisterCodeVO.from("one").getRegisterCode().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> RegisterCodeVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", RegisterCodeVO.from("one").toString());
  }
}
