package net.civeira.phylax.features.access.useraccesstemporalcode.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class FailedLoginAttemptsVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property failed login attempts of user access temporal code ")
  void test_failed_login_attempts_v_o_builder() {
    Assertions.assertFalse(FailedLoginAttemptsVO.nullValue().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(FailedLoginAttemptsVO.tryFrom(null).getValue().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals(1, FailedLoginAttemptsVO.from(1).getValue().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> FailedLoginAttemptsVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + 1 + "]", FailedLoginAttemptsVO.from(1).toString());
  }
}
