package net.civeira.phylax.features.access.useridentity.domain.gateway;

import java.util.List;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.features.access.relyingparty.domain.RelyingPartyReference;
import net.civeira.phylax.features.access.trustedclient.domain.TrustedClientReference;
import net.civeira.phylax.features.access.user.domain.UserReference;

class UserIdentityFilterUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test a entity reference contruction")
  void test_user_identity_filter_builder() {
    UserIdentityFilter filter = UserIdentityFilter.builder().build();
    Assertions.assertFalse(filter.getUid().isPresent(),
        "Uid must be empty if filter is build without it");
    filter.setUid("one");
    Assertions.assertEquals("one", filter.getUid().get(), "Uid must be the same as the assigned");
    Assertions.assertTrue(filter.getUids().isEmpty(),
        "Uid must be empty if filter is build without it");
    filter.setUids(List.of("one"));
    Assertions.assertEquals(List.of("one"), filter.getUids(),
        "Uids must be the same as the assigned");
    Assertions.assertFalse(filter.getSearch().isPresent(),
        "Search must be empty if filter is build without it");
    filter.setSearch("lookup");
    Assertions.assertEquals("lookup", filter.getSearch().get(),
        "Search must be the same as the assigned");
    Assertions.assertFalse(filter.getForAllAudiences().isPresent(),
        "ForAllAudiences must be empty if filter is build without it");
    filter.setForAllAudiences(true);
    Assertions.assertEquals(true, filter.getForAllAudiences().get(),
        "ForAllAudiences must be the same as the assigned");
    Assertions.assertFalse(filter.getUser().isPresent(),
        "User must be empty if filter is build without it");
    filter.setUser(UserReference.of("$ref"));
    Assertions.assertEquals(UserReference.of("$ref"), filter.getUser().get(),
        "User must be the same as the assigned");
    Assertions.assertTrue(filter.getUsers().isEmpty(),
        "Users must be empty if filter is build without it");
    filter.setUsers(List.of("one"));
    Assertions.assertEquals(List.of("one"), filter.getUsers(),
        "Users must be the same as the assigned");
    Assertions.assertFalse(filter.getRelyingParty().isPresent(),
        "RelyingParty must be empty if filter is build without it");
    filter.setRelyingParty(RelyingPartyReference.of("$ref"));
    Assertions.assertEquals(RelyingPartyReference.of("$ref"), filter.getRelyingParty().get(),
        "RelyingParty must be the same as the assigned");
    Assertions.assertTrue(filter.getRelyingPartys().isEmpty(),
        "RelyingPartys must be empty if filter is build without it");
    filter.setRelyingPartys(List.of("one"));
    Assertions.assertEquals(List.of("one"), filter.getRelyingPartys(),
        "RelyingPartys must be the same as the assigned");
    Assertions.assertFalse(filter.getTrustedClient().isPresent(),
        "TrustedClient must be empty if filter is build without it");
    filter.setTrustedClient(TrustedClientReference.of("$ref"));
    Assertions.assertEquals(TrustedClientReference.of("$ref"), filter.getTrustedClient().get(),
        "TrustedClient must be the same as the assigned");
    Assertions.assertTrue(filter.getTrustedClients().isEmpty(),
        "TrustedClients must be empty if filter is build without it");
    filter.setTrustedClients(List.of("one"));
    Assertions.assertEquals(List.of("one"), filter.getTrustedClients(),
        "TrustedClients must be the same as the assigned");
    Assertions.assertFalse(filter.getUserTenantTenantAccesible().isPresent(),
        "UserTenantTenantAccesible must be empty if filter is build without it");
    filter.setUserTenantTenantAccesible("one");
    Assertions.assertEquals("one", filter.getUserTenantTenantAccesible().get(),
        "UserTenantTenantAccesible must be the same as the assigned");
  }
}
