package net.civeira.phylax.features.access.useridentity.domain.valueobject;

import java.util.List;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;
import net.civeira.phylax.features.access.role.domain.RoleReference;
import net.civeira.phylax.features.access.useridentity.domain.Roles;

class RolesVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property roles of user identity ")
  void test_roles_v_o_builder() {
    Assertions.assertTrue(RolesVO.tryFrom(null).getRoles().isEmpty());
    List<Roles> list = List.of();
    Assertions.assertEquals(list, RolesVO.from(list).getRoles());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> RolesVO.tryFrom(falingInstance));
    Assertions.assertEquals(
        "[" + List.of(Roles.builder().uid("one").role(RoleReference.of("one")).version(1).build())
            + "]",
        RolesVO
            .from(List
                .of(Roles.builder().uid("one").role(RoleReference.of("one")).version(1).build()))
            .toString());
  }
}
